for (let i = 0; i < 30; i++) {
    const day = document.createElement('div');
    day.className = 'calendar-day';
    
    let sticker = '';
    
    if (i === 29) {
        sticker = `<div class="secret-sticker top-secret">ТОП СЕКРЕТНО</div>`;
    }

    else if (i % 7 === 0) {
        sticker = `<div class="secret-sticker">СЕКРЕТ</div>`;
    }

    day.innerHTML = `
        ${sticker}
        <div class="card-inner">
            <div class="card-front">
                <div class="day-number">${i+1}</div>
            </div>
            <div class="card-back">
                <div class="item-image">
                    <img src="${items[i].imageUrl}" alt="${items[i].name}">
                </div>
                <div class="item-name">${items[i].name}</div>
                <div class="item-rarity">${items[i].rarity}</div>
            </div>
        </div>
        `;
            
    calendar.appendChild(day);
}
        

const cards = document.querySelectorAll('.calendar-day');
cards.forEach(card => {
    card.addEventListener('mouseenter', () => {
        const sticker = card.querySelector('.secret-sticker');
        if (sticker) {
            sticker.style.transform = 'rotate(10deg) translateY(-10px)';
            sticker.style.transition = 'transform 0.5s ease';
        }
    });

    card.addEventListener('mouseleave', () => {
        const sticker = card.querySelector('.secret-sticker');
        if (sticker) {
            sticker.style.transform = 'rotate(0deg)';
        }
    });
});


