function createQuestsSection() {

    const questsSection = document.createElement('section');
    questsSection.className = 'quests-section';
            
    questsSection.innerHTML = `
        <div class="quests-container">
            <div class="quests-column">
                <div class="quests-header">
                    <h2 class="quests-title">Ежедневные задания</h2>
                </div>
                <div class="quests-list" id="daily-quests"></div>
                <div class="reset-info"></div>
                </div>
                <div class="quests-column">
                <div class="quests-header">
                    <h2 class="quests-title">Еженедельные задания</h2>
                </div>
                <div class="quests-list" id="weekly-quests"></div>
                <div class="reset-info"></div>
            </div>
        </div>
    `;
            
    const main = document.querySelector('main');
    main.appendChild(questsSection);
            
    fillQuests('daily-quests', dailyQuests);
    fillQuests('weekly-quests', weeklyQuests);

}
        
function fillQuests(containerId, quests) {

    const container = document.getElementById(containerId);
            
    quests.forEach(quest => {

        const questItem = document.createElement('div');
        questItem.className = `quest-item ${quest.completed ? 'quest-completed' : ''}`;

        questItem.innerHTML = `
            <div class="quest-info">
                <div class="quest-name">${quest.name}</div>
                <div class="quest-desc">${quest.desc}</div>
            </div>
            <div class="quest-reward ${quest.reward.includes('XP') ? 'xp-badge' : 'item-badge'}">
                ${quest.reward}
            </div>
        `;
                
        container.appendChild(questItem);
    
    });
}
        
function getTimeUntilReset(type) {

    const now = new Date();
    const utcHours = now.getUTCHours();
    const utcMinutes = now.getUTCMinutes();
    const utcSeconds = now.getUTCSeconds();

    if (type === 'daily') {
        const totalSecondsUntilReset = (24 * 3600) - (utcHours * 3600 + utcMinutes * 60 + utcSeconds);
        return totalSecondsUntilReset;

    } 

    else {

        const utcDay = now.getUTCDay();
        let daysUntilThursday = (4 - utcDay + 7) % 7;

        if (daysUntilThursday === 0 && utcHours === 0 && utcMinutes === 0 && utcSeconds === 0) {
            daysUntilThursday = 7;
        }
        
        const totalSecondsUntilReset = daysUntilThursday * 86400 - (utcHours * 3600 + utcMinutes * 60 + utcSeconds);
        return totalSecondsUntilReset;
    }
}

function startResetTimer(selector, type) {
    const timerElement = document.querySelector(selector);
    let seconds = getTimeUntilReset(type);
    
    function updateTimer() {
        seconds--;
        
        if (seconds <= 0) {
            seconds = getTimeUntilReset(type);

        }
        
        const days = Math.floor(seconds / 86400);
        const hours = Math.floor((seconds % 86400) / 3600);
        const minutes = Math.floor((seconds % 3600) / 60);
        const secs = seconds % 60;
        
        let timeString = '';
        if (days > 0) timeString += `${days}д `;
        if (hours > 0) timeString += `${hours}ч `;
        timeString += `${minutes}м`;
        
        timerElement.textContent = `Сброс через: ${timeString}`;

    }
    
    updateTimer();
    setInterval(updateTimer, 1000);

}

document.addEventListener('DOMContentLoaded', () => {

    createQuestsSection();
    
    startResetTimer('.quests-column:first-child .reset-info', 'daily');
    startResetTimer('.quests-column:last-child .reset-info', 'weekly');


});

